#include "gtest/gtest.h"

#define NOMAIN
#include "solver.cc"

TEST(Sample, Case1)
{
    EXPECT_EQ(solve("OZONETOWER"), "012");
}

TEST(Sample, Case2)
{
    EXPECT_EQ(solve("WEIGHFOXTOURIST"), "2468");
}

TEST(Sample, Case3)
{
    EXPECT_EQ(solve("OURNEONFOE"), "114");
}

TEST(Sample, Case4)
{
    EXPECT_EQ(solve("ETHER"), "3");
}

TEST(Stress, Each)
{
    EXPECT_EQ(solve("ZERO"), "0");
    EXPECT_EQ(solve("TWO"), "2");
    EXPECT_EQ(solve("FOUR"), "4");
    EXPECT_EQ(solve("SIX"), "6");
    EXPECT_EQ(solve("EIGHT"), "8");
    EXPECT_EQ(solve("THREE"), "3");
    EXPECT_EQ(solve("FIVE"), "5");
    EXPECT_EQ(solve("SEVEN"), "7");
    EXPECT_EQ(solve("NINE"), "9");
    EXPECT_EQ(solve("ONE"), "1");
}

TEST(Stress, Case1)
{
    EXPECT_EQ(solve(""), "");
}
